// 获取 Markdown 内容
let content = $json.data || $json.html || $json.output || '（没有可用内容）';

// ===== 添加 HTML 转 Markdown 并优化排版逻辑 =====
const htmlToMarkdown = (html) => {
  if (!html) return '';

  return html
    // 先替换常用结构
    .replace(/<p>(.*?)<\/p>/g, '$1\n')              // 段落
    .replace(/<strong>(.*?)<\/strong>/g, '**$1**')  // 加粗
    .replace(/<h1.*?>(.*?)<\/h1>/g, '# $1')        // 一级标题
    .replace(/<h2.*?>(.*?)<\/h2>/g, '## $1')       // 二级标题
    .replace(/<h3.*?>(.*?)<\/h3>/g, '### $1')      // 三级标题
    .replace(/<hr\s*\/?>/g, '---')                 // 分割线

    // 去掉空格实体
    .replace(/&nbsp;/g, ' ')

    // 删除常见无用标签，但保留内容
    .replace(/<(div|span|section|article|header|footer|aside)[^>]*>/gi, '')
    .replace(/<\/(div|span|section|article|header|footer|aside)>/gi, '')

    // 删除所有剩余 HTML 标签
    .replace(/<[^>]+>/g, '')

    // 去掉多余空行，保留段落间一行
    .replace(/\n{3,}/g, '\n\n')
    .trim();
};

// 调用转换函数
content = htmlToMarkdown(content);
// ===== 转换完成 =====

// 定义文件名
const fileName = `AI_Report_${new Date().toISOString().slice(0,10)}.md`;

// 转换为二进制数据
const binaryData = await this.helpers.prepareBinaryData(Buffer.from(content, 'utf8'), fileName, 'text/markdown');

// 返回数据，必须包含 json + binary
return [
  {
    json: {
      fileName,
      notice: 'Binary file attached under key "data"',
    },
    binary: {
      data: binaryData,   // 一定要叫 data，与 Send Email 里 Binary Property 对应
    },
  },
];
