// 获取 AI 分析内容
let content = $json.data || $json.html || $json.output || '（没有可用内容）';

// ===== HTML 转 Markdown 并优化排版 =====
const htmlToMarkdown = (html) => {
  if (!html) return '';
  return html
    .replace(/<p>(.*?)<\/p>/g, '$1\n')              // 段落
    .replace(/<strong>(.*?)<\/strong>/g, '**$1**')  // 加粗
    .replace(/<h1.*?>(.*?)<\/h1>/g, '# $1')         // 一级标题
    .replace(/<h2.*?>(.*?)<\/h2>/g, '## $1')        // 二级标题
    .replace(/<h3.*?>(.*?)<\/h3>/g, '### $1')       // 三级标题
    .replace(/<hr\s*\/?>/g, '---')                  // 分割线
    .replace(/&nbsp;/g, ' ')
    .replace(/<(div|span|section|article|header|footer|aside)[^>]*>/gi, '')
    .replace(/<\/(div|span|section|article|header|footer|aside)>/gi, '')
    .replace(/<[^>]+>/g, '')
    .replace(/\n{3,}/g, '\n\n')
    .trim();
};

// 转换 Markdown
content = htmlToMarkdown(content);

// ===== 生成简单 HTML 用于 PDF =====
let htmlContent = content.replace(/\n/g, '<br />');

// 保证 HTML 长度 ≥ 10
if (htmlContent.trim().length < 10) {
  htmlContent = '<p>无内容</p>';
}

// ===== 定义 Markdown 文件名 =====
const mdFileName = `AI_Report_${new Date().toISOString().slice(0,10)}.md`;

// 转换为 Markdown 二进制
const mdBinaryData = await this.helpers.prepareBinaryData(
  Buffer.from(content, 'utf8'),
  mdFileName,
  'text/markdown'
);

// ===== 返回 JSON + Markdown 二进制 =====
return [
  {
    json: {
      htmlContent  // 给 PDF 节点 Expression 使用
    },
    binary: {
      data: mdBinaryData  // Markdown 文件
    }
  }
];

