const pdfNode = $node["Convert HTML content to PDF"];
const pdfBase64 = pdfNode.json.response || "";
const filename = pdfNode.json.filename || "AI_Report.pdf";

const base64ToUse = pdfBase64.length > 50 ? pdfBase64 : Buffer.from("<p>无内容</p>").toString("base64");
const buffer = Buffer.from(base64ToUse, "base64");
const pdfBinary = await this.helpers.prepareBinaryData(buffer, filename, "application/pdf");

return [
  {
    json: { notice: "PDF binary generated successfully" },
    binary: { data: pdfBinary }
  }
];
